//https://github.com/proposal-signals/signal-polyfill
import { Signal } from "signal-polyfill";
export function signal(target) {
    const { get } = target;
    return {
        get() {
            return get.call(this).get();
        },
        set(value) {
            get.call(this).set(value);
        },
        init(value) {
            return new Signal.State(value);
        },
    };
}
